-- Create "general_ledger_accounts" table
CREATE TABLE "general_ledger_accounts" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "status" character varying NOT NULL DEFAULT 'A', "account_number" character varying NOT NULL, "account_type" character varying NOT NULL, "cash_flow_type" character varying NULL, "account_sub_type" character varying NULL, "account_class" character varying NULL, "balance" double precision NULL, "interest_rate" double precision NULL, "date_opened" timestamptz NOT NULL, "date_closed" timestamptz NULL, "notes" character varying NULL, "is_tax_relevant" boolean NOT NULL DEFAULT false, "is_reconciled" boolean NOT NULL DEFAULT false, "business_unit_id" uuid NOT NULL, "organization_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "general_ledger_accounts_business_units_business_unit" FOREIGN KEY ("business_unit_id") REFERENCES "business_units" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "general_ledger_accounts_organizations_organization" FOREIGN KEY ("organization_id") REFERENCES "organizations" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
-- Create index "general_ledger_accounts_account_number_key" to table: "general_ledger_accounts"
CREATE UNIQUE INDEX "general_ledger_accounts_account_number_key" ON "general_ledger_accounts" ("account_number");
