-- Create "billing_controls" table
CREATE TABLE "billing_controls" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "remove_billing_history" boolean NOT NULL DEFAULT false, "auto_bill_shipment" boolean NOT NULL DEFAULT false, "auto_mark_ready_to_bill" boolean NOT NULL DEFAULT false, "validate_customer_rates" boolean NOT NULL DEFAULT false, "auto_bill_criteria" character varying NOT NULL DEFAULT 'MarkedReadyToBill', "shipment_transfer_criteria" character varying NOT NULL DEFAULT 'ReadyToBill', "organization_id" uuid NOT NULL, "business_unit_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "billing_controls_business_units_business_unit" FOREIGN KEY ("business_unit_id") REFERENCES "business_units" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "billing_controls_organizations_organization" FOREIGN KEY ("organization_id") REFERENCES "organizations" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
