-- Create "revenue_codes" table
CREATE TABLE "revenue_codes" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "code" character varying NOT NULL, "description" text NOT NULL, "business_unit_id" uuid NOT NULL, "organization_id" uuid NOT NULL, "expense_account_id" uuid NULL, "revenue_account_id" uuid NULL, PRIMARY KEY ("id"), CONSTRAINT "revenue_codes_business_units_business_unit" FOREIGN KEY ("business_unit_id") REFERENCES "business_units" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "revenue_codes_general_ledger_accounts_expense_account" FOREIGN KEY ("expense_account_id") REFERENCES "general_ledger_accounts" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "revenue_codes_general_ledger_accounts_revenue_account" FOREIGN KEY ("revenue_account_id") REFERENCES "general_ledger_accounts" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "revenue_codes_organizations_organization" FOREIGN KEY ("organization_id") REFERENCES "organizations" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
-- Create index "revenuecode_code_organization_id" to table: "revenue_codes"
CREATE UNIQUE INDEX "revenuecode_code_organization_id" ON "revenue_codes" ("code", "organization_id");
