-- Create "accessorial_charges" table
CREATE TABLE "accessorial_charges" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "status" character varying NOT NULL DEFAULT 'A', "code" character varying NOT NULL, "description" character varying NULL, "is_detention" boolean NOT NULL DEFAULT false, "method" character varying NOT NULL, "amount" numeric(19,4) NOT NULL DEFAULT 0, "business_unit_id" uuid NOT NULL, "organization_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "accessorial_charges_business_units_business_unit" FOREIGN KEY ("business_unit_id") REFERENCES "business_units" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "accessorial_charges_organizations_organization" FOREIGN KEY ("organization_id") REFERENCES "organizations" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
-- Create index "accessorialcharge_code_organization_id" to table: "accessorial_charges"
CREATE UNIQUE INDEX "accessorialcharge_code_organization_id" ON "accessorial_charges" ("code", "organization_id");
