-- Create "shipment_comments" table
CREATE TABLE "shipment_comments" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "comment" text NOT NULL, "comment_type_id" uuid NOT NULL, "shipment_id" uuid NOT NULL, "created_by" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "shipment_comments_comment_types_shipment_comments" FOREIGN KEY ("comment_type_id") REFERENCES "comment_types" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "shipment_comments_shipments_shipment_comments" FOREIGN KEY ("shipment_id") REFERENCES "shipments" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "shipment_comments_users_shipment_comments" FOREIGN KEY ("created_by") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
