// AccessorialChargeRelationChain provides a fluent API for building nested relationship chains
type AccessorialChargeRelationChain struct {
	relations []string
	options   map[string]func(*bun.SelectQuery) *bun.SelectQuery
}

// NewAccessorialChargeRelationChain creates a new relation chain builder
func NewAccessorialChargeRelationChain() *AccessorialChargeRelationChain {
	return &AccessorialChargeRelationChain{
		relations: []string{},
		options:   make(map[string]func(*bun.SelectQuery) *bun.SelectQuery),
	}
}

// Add adds a relation to the chain with optional configuration
func (rc *AccessorialChargeRelationChain) Add(relation string, opts ...func(*bun.SelectQuery) *bun.SelectQuery) *AccessorialChargeRelationChain {
	rc.relations = append(rc.relations, relation)
	if len(opts) > 0 {
		rc.options[relation] = func(q *bun.SelectQuery) *bun.SelectQuery {
			for _, opt := range opts {
				q = opt(q)
			}
			return q
		}
	}
	return rc
}

// Build builds the relation chain
func (rc *AccessorialChargeRelationChain) Build() []string {
	return rc.relations
}

// Apply applies the relation chain to a query
func (rc *AccessorialChargeRelationChain) Apply(q *bun.SelectQuery) *bun.SelectQuery {
	for _, rel := range rc.relations {
		if opt, ok := rc.options[rel]; ok {
			q = q.Relation(rel, opt)
		} else {
			q = q.Relation(rel)
		}
	}
	return q
}

