-- Create "invoice_controls" table
CREATE TABLE "invoice_controls" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "invoice_number_prefix" character varying NOT NULL DEFAULT 'INV-', "credit_memo_number_prefix" character varying NOT NULL DEFAULT 'CM-', "invoice_terms" text NULL, "invoice_footer" text NULL, "invoice_logo_url" character varying NULL, "invoice_date_format" character varying NOT NULL DEFAULT 'InvoiceDateFormatMDY', "invoice_due_after_days" smallint NOT NULL DEFAULT 30, "invoice_logo_width" smallint NOT NULL DEFAULT 100, "show_amount_due" boolean NOT NULL DEFAULT true, "attach_pdf" boolean NOT NULL DEFAULT true, "show_invoice_due_date" boolean NOT NULL DEFAULT true, "business_unit_id" uuid NOT NULL, "organization_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "invoice_controls_business_units_business_unit" FOREIGN KEY ("business_unit_id") REFERENCES "business_units" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "invoice_controls_organizations_invoice_control" FOREIGN KEY ("organization_id") REFERENCES "organizations" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create index "invoice_controls_organization_id_key" to table: "invoice_controls"
CREATE UNIQUE INDEX "invoice_controls_organization_id_key" ON "invoice_controls" ("organization_id");
