-- Create "hazardous_materials" table
CREATE TABLE "hazardous_materials" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "name" character varying NOT NULL, "hazard_class" character varying NOT NULL DEFAULT 'HazardClass1And1', "erg_number" character varying NULL, "description" text NULL, "packing_group" character varying NULL, "proper_shipping_name" text NULL, "business_unit_id" uuid NOT NULL, "organization_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "hazardous_materials_business_units_business_unit" FOREIGN KEY ("business_unit_id") REFERENCES "business_units" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "hazardous_materials_organizations_organization" FOREIGN KEY ("organization_id") REFERENCES "organizations" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
-- Create "commodities" table
CREATE TABLE "commodities" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "status" character varying NOT NULL DEFAULT 'A', "name" character varying NOT NULL, "is_hazmat" boolean NOT NULL DEFAULT false, "unit_of_measure" character varying NULL, "min_temp" double precision NULL, "max_temp" double precision NULL, "set_point_temp" double precision NULL, "description" text NULL, "business_unit_id" uuid NOT NULL, "organization_id" uuid NOT NULL, "hazardous_material_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "commodities_business_units_business_unit" FOREIGN KEY ("business_unit_id") REFERENCES "business_units" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "commodities_hazardous_materials_commodities" FOREIGN KEY ("hazardous_material_id") REFERENCES "hazardous_materials" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "commodities_organizations_organization" FOREIGN KEY ("organization_id") REFERENCES "organizations" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
