-- Create "equipment_types" table
CREATE TABLE "equipment_types" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "status" character varying NOT NULL DEFAULT 'A', "name" character varying NOT NULL, "description" text NULL, "cost_per_mile" numeric(10,2) NULL, "equipment_class" character varying NOT NULL DEFAULT 'Undefined', "fixed_cost" numeric(10,2) NULL, "variable_cost" numeric(10,2) NULL, "height" numeric(10,2) NULL, "length" numeric(10,2) NULL, "width" numeric(10,2) NULL, "weight" numeric(10,2) NULL, "idling_fuel_usage" numeric(10,2) NULL, "exempt_from_tolls" boolean NOT NULL DEFAULT false, "business_unit_id" uuid NOT NULL, "organization_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "equipment_types_business_units_business_unit" FOREIGN KEY ("business_unit_id") REFERENCES "business_units" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "equipment_types_organizations_organization" FOREIGN KEY ("organization_id") REFERENCES "organizations" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
-- Create index "equipmenttype_name_organization_id" to table: "equipment_types"
CREATE UNIQUE INDEX "equipmenttype_name_organization_id" ON "equipment_types" ("name", "organization_id");
