-- Create "worker_comments" table
CREATE TABLE "worker_comments" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "business_unit_id" uuid NOT NULL, "organization_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "worker_comments_business_units_business_unit" FOREIGN KEY ("business_unit_id") REFERENCES "business_units" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "worker_comments_organizations_organization" FOREIGN KEY ("organization_id") REFERENCES "organizations" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
-- Create "worker_profiles" table
CREATE TABLE "worker_profiles" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "date_of_birth" timestamptz NOT NULL, "license_number" timestamptz NULL, "license_state_id" uuid NULL, "mvr_due_date" date NULL, "worker_id" uuid NOT NULL, "business_unit_id" uuid NOT NULL, "organization_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "worker_profiles_business_units_business_unit" FOREIGN KEY ("business_unit_id") REFERENCES "business_units" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "worker_profiles_organizations_organization" FOREIGN KEY ("organization_id") REFERENCES "organizations" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "worker_profiles_workers_worker_profile" FOREIGN KEY ("worker_id") REFERENCES "workers" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create index "worker_profiles_worker_id_key" to table: "worker_profiles"
CREATE UNIQUE INDEX "worker_profiles_worker_id_key" ON "worker_profiles" ("worker_id");
