export const isValidResource = (resource: string): boolean => {
  return [
    'accessorial_charge',
    'account_type',
    'accounting_control',
    'audit_entry',
    'billing_control',
    'commodity',
    'customer',
    'data_retention',
    'dedicated_lane',
    'dedicated_lane_suggestion',
    'dispatch_control',
    'distance_override',
    'document_type',
    'email_profile',
    'equipment_manufacturer',
    'equipment_type',
    'fiscal_period',
    'fiscal_year',
    'fleet_code',
    'gl_account',
    'hazardous_material',
    'hazmat_segregation_rule',
    'hold_reason',
    'journal_entry',
    'location',
    'location_category',
    'organization',
    'pattern_config',
    'service_type',
    'shipment',
    'shipment_comment',
    'shipment_control',
    'shipment_type',
    'tractor',
    'trailer',
    'user',
    'variable',
    'variable_format',
    'worker',
    'worker_pto',
  ].includes(resource);
};

export const isValidOperation = (operation: PermissionOperation): boolean => {
  return Object.values(PermissionOperations).includes(operation);
};
